import { redirect } from "react-router-dom";

export const getAuthToken = () => {
  const response = localStorage.getItem("token");
  return response;
};

export const tokenLoader = () => {
  return getAuthToken();
};

export const checkAuthLoader = () => {
  const token = getAuthToken();

  if (!token) {
    return redirect("/");
  }

  return null;
};
