import Todo from "../models/todo";
import classess from "./TodoItem.module.css";
const TodoItem = ({
  item,
  onRemoveTodo,
}: {
  item: Todo;
  onRemoveTodo: (id: string) => void;
}) => {
  return (
    <li className={classess.item}>
      <button onClick={onRemoveTodo(item.id)}> {item.text}</button>
    </li>
  );
};
export default TodoItem;
